import { useState } from "react";
import { useEffect } from "react";
import Img from "./Img";

export function CatList_Container() {

	const [catlist, setCatlist] = useState([]);
	const[selectedCatImag,setselectedCatImag] = useState('');
	const {selectName,setselectName} = useState('');

	function handleSubmit(event) {
		event.preventDefault();
		// console.log(event.target)
		setselectName(event.target.name);

	}

	fetch('https://api.thecatapi.com/v1/images/search?breed_ids='+{selectName})
	.then((res) => {res.json()})
	.then((json) =>{
		console.log(json[0].url);
		return setselectedCatImag(json[0].url);
	})

	useEffect(() => {
		fetch('https://api.thecatapi.com/v1/breeds')
		.then(function(res) {
			return res.json();
		})
		.then(function(json) {
			return setCatlist(Object(json))
		})
	},[])

	return(
		<>
		<Img name={catlist}
		handleSubmit={handleSubmit}/>
		</>
	)

}

export default CatList_Container;
