import React from "react";
import { useState } from "react";

export function Garllery(props) {
	const url = "https://cdn2.thecatapi.com/images/dmr.jpg";
	const [catImg, setCatImg] = useState(url);
	const names = props.name;
	const cats = props.selectedCat;

	const nameList = names.map((list) => {
		return <option key={list.name} value={list.id} origin={list.origin}>{list.name}</option>
	})

	function getImg() {
		fetch('https://api.thecatapi.com/v1/images/search')
			.then(function (res) {
				// console.log(res)
				return res.json();
			})
			.then((data) => {
				// console.log(data[0].url)
				return setCatImg(data[0].url);
			})
	}


	return (
		<section className="gellery">
			<div className="container">
				<p>猫の画像</p>
				<img src={catImg} ></img>
				<button onClick={getImg}>違う猫</button>
			</div>

			<form onChange={props.handleSubmit}>
				<select>
					<option value="" id="" selected>ねこ</option>
					{nameList}
				</select>
			</form>

			<div className="Catlist">
				{/* {catImgList} */}
				<img key={cats} src={cats} on></img>
				<p>{names.name}</p>
			</div>
			<div>
				<p>ねこの名前</p>
				<p>猫の情報</p>
			</div>

		</section>


	)
}

export default Img;
