	function getImg() {
		fetch('https://api.thecatapi.com/v1/images/search')
			.then(function (res) {
				// console.log(res)
				return res.json();
			})
			.then((data) => {
				// console.log(data[0].url)
				return setCatImg(data[0].url);
			})
	}


	return (
		<section>
			<p>猫の画像</p>
			<img src={catImg} ></img>
			<button onClick={getImg} >違う猫</button>
			<button >元の猫</button>

			<form onChange={props.handleSubmit}>
				<select>
					<option value="" id="" selected>ねこ</option>
					{nameList}
				</select>
			</form>
			<div className="Catlist">
				{/* {catImgList} */}
