import Slick from "react-slick";

import React, { useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
// import "swiper/css";
// import "swiper/css/navigation";
// import "swiper/css/pagination";

// import "./styles.css";

// import required modules
import { Navigation, Pagination, Mousewheel, Keyboard } from "swiper";




export function Img(props) {
	const url = "https://cdn2.thecatapi.com/images/dmr.jpg";
	const [catImg, setCatImg] = useState(url);

	const names = props.name;
	const nameList = names.map((list) => {
		return <option key={list.name} value={list.id} origin={list.origin}>{list.name}</option>
	})


	const cats = props.selectedCat;





	// async function catImgList () {
	// 	await cats.map((img) => {
	// 	console.log(img)
	// return <img key={list} src={list}></img>
	// 	})
	// }
	//  catImgList();



	function getImg() {
		fetch('https://api.thecatapi.com/v1/images/search')
			.then(function (res) {
				// console.log(res)
				return res.json();
			})
			.then((data) => {
				// console.log(data[0].url)
				return setCatImg(data[0].url);
			})
	}


	return (
		<section>
			<p>猫の画像</p>
			<img src={catImg} ></img>
			<button onClick={getImg} >違う猫</button>
			<button >元の猫</button>

			<form onChange={props.handleSubmit}>
				<select>
					<option value="" id="" selected>ねこ</option>
					{nameList}
				</select>
			</form>
			<div className="Catlist">
				{/* {catImgList} */}
				<img key={cats} src={cats} on></img>

				<Swiper
					cssMode={true}
					navigation={true}
					pagination={true}
					mousewheel={true}
					keyboard={true}
					modules={[Navigation, Pagination, Mousewheel, Keyboard]}
					className="mySwiper"
				>
					<SwiperSlide>Slide 1</SwiperSlide>
					<SwiperSlide>Slide 2</SwiperSlide>
					<SwiperSlide>Slide 3</SwiperSlide>
					<SwiperSlide>Slide 4</SwiperSlide>
					<SwiperSlide>Slide 5</SwiperSlide>
					<SwiperSlide>Slide 6</SwiperSlide>
					<SwiperSlide>Slide 7</SwiperSlide>
					<SwiperSlide>Slide 8</SwiperSlide>
					<SwiperSlide>Slide 9</SwiperSlide>
				</Swiper>
				<p>{names.name}</p>
			</div>

		</section>
	)
}

export default Img;
