import { useState } from "react"

export function Gellery(props) {

	const url = "https://cdn2.thecatapi.com/images/dmr.jpg";
	const [catImg, setCatImg] = useState(url);
	const names = props.name;
	const cats = props.selectedCat;
	console.log(cats)
	const nameList = names.map((list) => {
		return <option key={list.name} value={list.id} name={list.name} origin={list.origin}>{list.name}</option>
	})

	function getImg() {
		fetch('https://api.thecatapi.com/v1/images/search')
			.then(function (res) {
				return res.json();
			})
			.then((data) => {
				return setCatImg(data[0].url);
			})
	}


	return (
		<section className="gellery">
			<div className="container">
				<img src={catImg} ></img>
				<button onClick={getImg}>Random</button>
			</div>
				<form onChange={props.handleSubmit}>
					<select>
						<option value="" id="" selected>Pick</option>
						{nameList}
					</select>
				</form>
		</section>


	)
}

export default Gellery;
