import React from "react";
import { useState } from "react";



export function Img (props) {
	const url = "https://cdn2.thecatapi.com/images/dmr.jpg";
	const [catImg, setCatImg] = useState(url);

	const names = props.name;
	const nameList = names.map((list) =>{
		// console.log(list.id)
		console.log(props)
		return <option key={list.name} value={list.name} id={list.id} name={list.name}>{list.name}</option>
	}	)

	function getImg () {
	fetch('https://api.thecatapi.com/v1/images/search')
	.then(function(res) {
			// console.log(res)
			return res.json();
	})
	.then((data) => {
			// console.log(data[0].url)
			return setCatImg(data[0].url);
	})}


	return(
	<section>
		<p>猫の画像</p>
		<img src ={catImg} ></img>
		<button onClick={getImg} >違う猫</button>
		<button >元の猫</button>

		<form onChange={props.handleSubmit}>
			<select>
				<option value="" id="" selected>ねこ</option>
				{nameList}
			</select>
		</form>

	</section>
	)
}

export default Img;
